import React from "react";

export default function Card({
  bgMainColor,
  bgSecondaryColor,
  cardTitle,
  myImg,
  clickStatus,
  myFn,
}) {
  return (
    <div
      className={`flex flex-col justify-center items-center gap-3 border-2  border-slate-400 text-white rounded-lg ${bgMainColor} cursor-pointer hover:border-red-400 hover:border-4`}
      onClick={myFn}
    >
      <div
        className={`${bgSecondaryColor} h-3/4 w-5/6 rounded-lg flex justify-center items-center`}
      >
        <img
          src={myImg}
          className="w-[85%] h-[85%] rounded-lg"
          id={clickStatus}
        />
      </div>
      <p className="text-xl"> {cardTitle} </p>
    </div>
  );
}
